INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('DosPuzzle', 'Server', 'Denial-of-Service protection puzzle', 0, 'When enabled the server adds a puzzle to validate Ajax sessions in the first Ajax request. This is a simple measure which avoids Denial-of-Service attacks on Ajax sessions.\r\n\r\nDefault value: Off');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('JavascriptDebug', 'Server', 'Debug javascript mode', 3, 'Enable this only if you are a developer and to debug javascript.\r\n\r\nPossible values:\r\n1 = (Off)JavaScript errors are caught and a simple error message is printed to indicate that something is terribly wrong(default, recommended)\r\n2 = (On)JavaScript errors are caught but also a stack trace is printed, useful for diagnosing a problem in production\r\n3 = (On)JavaScript errors are not caught but the browser built-in debug options are used');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('LogDebugLevel', 'Logging', 'Log debugging messages', 0, 'When enabled debugging events/messages will be included in the log file. These notices only gives information for developers and might not be meaningful to end users.\r\n\r\nRecommended Value: Off');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('LogDirectory', 'Logging', 'Logging file directory', 4, 'Location to where the file in which logs will be created is. Can be a full path or even a relative path.\r\n\r\nWhen this is left empty, no log file will be created but disabling file logging is highly not recommended.\r\nDefault Value: ./logs');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('LogErrorLevel', 'Logging', 'Log error messages', 0, 'When enabled errors will be included in the log file. \r\n\r\nRecommended Value: On');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('LogInfoLevel', 'Logging', 'Log informational notices', 0, 'When enabled informational notices will be included in the log file. These notices only gives information rather than reporting a warning or an error.');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('LogSecureLevel', 'Logging', 'Log security events', 0, 'When enabled security related messages will be included in the log file.\r\n\r\nRecommended Value: On');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('LogWarnLevel', 'Logging', 'Log warning notices', 0, 'When enabled warnings will be included in the log file. These warnings indicate that there was something could have caused an error and should be considered.\r\n\r\nRecommended Value: On');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('MaxRequestSize', 'Server', 'Max request size', 3, 'Maximum request size in Kb. This have to be increased to allow uploading larger sized files.');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('ReverseProxy', 'Server', 'Behind reverse proxy', 0, 'Enable this if the server is behind a reverse proxy. If you do not know what this means then disable it.\r\n\r\nDefault Value: Off');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('SessionTimeout', 'Server', 'Session timeout delay', 3, 'Session timeout delay in seconds. When a session remains inactive for this amount of time, it will be cleaned up.\r\nRecommended value: 600 (1 minute)');
INSERT INTO `configuration` (`Name`, `Application`, `Title`, `Type`, `Details`) VALUES ('SessionTracking', 'Server', 'Session tracking strategy', 3, 'Possible values:\r\n1 = Cookies are used if available, otherwise URL rewriting for tracking session(default)\r\n2 = Only URL rewriting for tracking session');
